import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

export interface Juego {
  id: number;
  name: string;
  imageUrl: string;
  description: string;
}

@Injectable({
  providedIn: 'root'
})
export class JuegosService {
  // Simula una base de datos de juegos para el ejemplo
  private juegos: Juego[] = [
    {id: 1, name: 'Juego de la Oca', imageUrl: 'data:image/jpeg;base64,', description: 'El Juego de la Oca es un juego de mesa clásico español que se juega con un tablero con forma de espiral y dados.'},
    {id: 2, name: 'Parchís', imageUrl: 'data:image/png;base64,', description: 'El Parchís es un juego de mesa de origen español similar al Ludo, donde los jugadores mueven sus fichas siguiendo el lanzamiento de dados.'},
    {id: 3, name: 'Dominó', imageUrl: 'data:image/jpeg;base64,', description: 'El Dominó es un juego de mesa de origen europeo que se juega con fichas rectangulares con puntos que representan números.'},
    {id: 4, name: 'Mus', imageUrl: 'data:image/jpeg;base64,', description: 'El Mus es un juego de cartas de origen español muy popular en el País Vasco y otras regiones, donde los jugadores forman parejas y compiten por puntos.'},
    {id: 5, name: 'Chinchón', imageUrl: 'https://play-lh.googleusercontent.com/A3hLW9AAN1IAbaFiV-5Dq1D6sgwrXeMqH_SLLDNhGU4k1QNh6Pz9RrBV2a6l2f90mbIq', description: 'El Chinchón es un juego de cartas español similar al Gin Rummy, donde los jugadores intentan formar combinaciones de cartas en su mano.'},
    {id: 6, name: 'Ajedrez', imageUrl: 'https://m.media-amazon.com/images/I/61Y4EJ5vbaL._AC_UF894,1000_QL80_.jpg', description: 'El Ajedrez es un juego de estrategia y habilidad que se juega en un tablero de 64 casillas con piezas blancas y negras.'},
    {id: 7, name: 'Baraja Española', imageUrl: 'data:image/jpeg;base64,', description: 'La Baraja Española es un conjunto de 40 o 48 cartas utilizadas en diversos juegos de cartas tradicionales españoles.'},
    {id: 8, name: 'Truc', imageUrl: 'https://http2.mlstatic.com/D_NQ_NP_828153-MLA51742828853_092022-O.webp', description: 'El Truc es un juego de cartas muy popular en la Comunidad Valenciana, donde los jugadores forman equipos y compiten por puntos.'},
    {id: 9, name: 'Escoba', imageUrl: 'https://www.nhfournier.es/wp-content/uploads/2017/10/brisca-baraja-cartas.jpg', description: 'La Escoba es un juego de cartas español donde los jugadores intentan capturar cartas del tablero para sumar puntos.'}
  ];

  constructor() {
  }

  // Obtiene los detalles de un juego por ID
  obtenerJuegoPorId(id: number): Juego | undefined {
    return this.juegos.find(juego => juego.id === id);
  }

  obtenerJuegos(): Observable<Juego[]> {
    return new BehaviorSubject<Juego[]>(this.juegos).asObservable();
  }
}
