import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

export interface Juego {
  id: number;
  name: string;
  imageUrl: string;
  description: string;
}

@Injectable({
  providedIn: 'root'
})
export class JuegosService {
  // Simula una base de datos de juegos para el ejemplo
  private juegos: Juego[] = [
    {id: 1, name: 'Juego de la Oca', imageUrl: 'data:image/jpeg;base64,', description: 'El Juego de la Oca es un juego de mesa clásico español que se juega con un tablero con forma de espiral y dados.'},
    {id: 2, name: 'Parchís', imageUrl: 'https://www.mundijuegos.com/multimedia//juegos/569/parchis-new_2.jpg', description: 'El Parchís es un juego de mesa de origen español similar al Ludo, donde los jugadores mueven sus fichas siguiendo el lanzamiento de dados.'},
    {id: 3, name: 'Dominó', imageUrl: 'https://m.media-amazon.com/images/I/61ROxwzvLrL._AC_SY679_.jpg', description: 'El Dominó es un juego de mesa de origen europeo que se juega con fichas rectangulares con puntos que representan números.'},
    {id: 4, name: 'Mus', imageUrl: 'https://www.aprenderpoker.net/wp-content/uploads/2020/03/Mus.jpg', description: 'El Mus es un juego de cartas de origen español muy popular en el País Vasco y otras regiones, donde los jugadores forman parejas y compiten por puntos.'},
    {id: 5, name: 'Chinchón', imageUrl: 'https://m.media-amazon.com/images/I/51umEXgzm8L.jpg', description: 'El Chinchón es un juego de cartas español similar al Gin Rummy, donde los jugadores intentan formar combinaciones de cartas en su mano.'},
    {id: 6, name: 'Ajedrez', imageUrl: 'https://m.media-amazon.com/images/I/71MNyzGtnhS._AC_SY679_.jpg', description: 'El Ajedrez es un juego de estrategia y habilidad que se juega en un tablero de 64 casillas con piezas blancas y negras.'},
    {id: 7, name: 'Baraja Española', imageUrl: 'https://m.media-amazon.com/images/I/51wumPBy4QL.jpg', description: 'La Baraja Española es un conjunto de 40 o 48 cartas utilizadas en diversos juegos de cartas tradicionales españoles.'},
    {id: 8, name: 'Truc', imageUrl: 'https://m.media-amazon.com/images/I/51LroE4C70L.jpg', description: 'El Truc es un juego de cartas muy popular en la Comunidad Valenciana, donde los jugadores forman equipos y compiten por puntos.'},
    {id: 9, name: 'Escoba', imageUrl: 'https://m.media-amazon.com/images/I/51+KlBby4nL.jpg', description: 'La Escoba es un juego de cartas español donde los jugadores intentan capturar cartas del tablero para sumar puntos.'}
  ];

  constructor() {
  }

  // Obtiene los detalles de un juego por ID
  obtenerJuegoPorId(id: number): Juego | undefined {
    return this.juegos.find(juego => juego.id === id);
  }

  obtenerJuegos(): Observable<Juego[]> {
    return new BehaviorSubject<Juego[]>(this.juegos).asObservable();
  }
}
